import React, { Component } from 'react';

import '../css/App.css';

let mainMenuItem;

class MainMenu extends Component {
  componentDidMount = () => {
    function mainMenuItemClick() {
      const selectedClass = this.classList[1];
      console.log(`i have been clicked -> ${selectedClass}`);

      display.classList.forEach((item) => {
        if (
          item !== selectedClass &&
          item[0] + item[1] + item[2] + item[3] !== 'hide'
        ) {
          // display.classList.add(`hide-${item}`);
        }
      });

      display.classList.add(`${selectedClass}`);
      display.classList.remove(`hide-${selectedClass}`);
    }

    mainMenuItem = document.querySelectorAll('.mainMenuItem');

    mainMenuItem.forEach((item) =>
      item.addEventListener('click', mainMenuItemClick)
    );
  };
  render() {
    function onClick(e) {
      // const type = this._reactInternals.elementType.name;
      console.log('im clicked', e);
      console.log(mainMenuItem);
    }
    return (
      <>
        <div className="mainMenuItem Singleplayer" onClick={onClick}>
          <p className="mMP">Single-Player</p>
        </div>
        <div className="mainMenuItem Multiplayer">
          <p className="mMP">Enter Multiplayer Lobby</p>
        </div>
        <div className="mainMenuItem Tutorial">
          <p className="mMP">Tutorial</p>
        </div>
        <div className="mainMenuItem CoatOfArms">
          <p className="mMP">Coat Of Arms</p>
        </div>
        <div className="mainMenuItem ArmySetup">
          <p className="mMP">Army Setup</p>
        </div>
        <div className="mainMenuItem Options">
          <p className="mMP">Options</p>
        </div>
        <div className="mainMenuItem Instructions">
          <p className="mMP">Instructions</p>
        </div>
        <div className="mainMenuItem Highscores">
          <p className="mMP">Highscores</p>
        </div>
        <div className="mainMenuItem Achievements">
          <p className="mMP">Achievements</p>
        </div>
        <div className="mainMenuItem Quit">
          <p className="mMP">Quit</p>
        </div>
      </>
    );
  }
}

export default MainMenu;
