const displayReducer = (state = ['display', 'mainMenu'], action) => {
  let className;
  state.forEach((item) => {
    console.log(item);
    className += `${item} `;
  });
  if (!action.payload) {
    state = [...state];
  }
  switch (action.type) {
    case 'SET_DISPLAY':
      console.log(action.payload);

      return;
    default:
      return className;
  }
};

export default displayReducer;
