const displayReducer = (state = 'display mainMenu', action) => {
  switch (action.type) {
    case 'SET_DISPLAY':
      return action.payload;
    case 'ADD_DISPLAY':
      return action.payload;
    case 'REMOVE_DISPLAY':
      return action.payload;
    default:
      return state;
  }
};

export default displayReducer;
